import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css'
import { Button, Card, Form } from 'react-bootstrap'

function SearchBrews() {
  function handleFood() {
    console.log('food')
  }

  function handleAbv(e) {
    if (e.target.value === 'abv_lt=5.0') {
      fetch('https://api.punkapi.com/v2/beers?abv_lt=5.0&per_page=5')
        .then((response) => response.json())
        .then((data) => console.log(data))
    }
  }

  return (
    <div id="parent">
      <Form.Select
        onChange={handleAbv}
        id="selection"
        aria-label="Default select example"
      >
        <option>Open this select menu</option>
        <option value="abv_lt=5.0">Light</option>
        <option value="abv_gt=5.0abv_lt=7.0">Medium</option>
        <option value="abv_gt=7.0">Heavy</option>
      </Form.Select>
      <Form.Select
        onChange={handleFood}
        id="selection"
        aria-label="Default select example"
      >
        <option>Open this select menu</option>
        <option value="temp">Sweet</option>
        <option value="temp2">Spicy</option>
        <option value="temp3">Savory</option>
      </Form.Select>
    </div>
  )
}
export default SearchBrews
