import React, { useState, useEffect } from 'react'

function LocalPubs() {
  const [userEntry, setUserEntry] = useState('')
  const [breweryReturn, setBreweryReturn] = useState([])
  const [drinkPic, setDrinkPic] = useState([])
  const [drinkName, setDrinkName] = useState([])
  const [isSelected, setIsSelected] = useState(false)

  useEffect(() => {
    fetch('https://thecocktaildb.com/api/json/v1/1/random.php')
      .then((response) => response.json())
      .then((randoCocktail) => {
        let cocktailName = randoCocktail.drinks[0].strDrink
        let cocktailPic = randoCocktail.drinks[0].strDrinkThumb
        setIsSelected(true)
        setDrinkName(cocktailName)
        setDrinkPic(cocktailPic)
      })
  }, [])

  function displayCocktail() {}

  function handleSubmit(e) {
    e.preventDefault()

    fetch(`https://api.openbrewerydb.org/breweries?by_postal=${userEntry}`)
      .then((response) => response.json())
      .then((cities) => setBreweryReturn(cities))
  }

  function handleZipInput(e) {
    setUserEntry(e.target.value)
  }

  return (
    <div>
      <form id="local-breweries" onSubmit={handleSubmit}>
        <label className="yourZip">Enter Zip</label>
        <input type="text" onChange={handleZipInput} value={userEntry} />
        <button type="submit">GO</button>
      </form>
      <button>Cocktail</button>
      {drinkName}
      <img src={drinkPic} alt="A Lovely Libation"></img>
      <ul id="pub-list">
        {breweryReturn.map((brewery) => (
          <li key={brewery.id}>
            {brewery.name} ‚óè {brewery.street}
          </li>
        ))}
      </ul>
    </div>
  )
}

export default LocalPubs
